class Bot:

    def __init__(self, rpc_endpoint: str, t2s: str, t2b: str, gas: int=5, slippage:int = 0, test: bool = False) -> None:

        self.rpc_endpoint = rpc_endpoint
        self.wallet_addr = os.getenv("WALLET_ADDR")
        self.private_key = os.getenv('PRIVATE_KEY')
        self.gas = gas # as in gwei
        self.test = test
        self.slippage = slippage
    
        self.token_2_spend_addr = t2s
        self.token_2_buy_addr = t2b

        self.factory_addr = addr["pancakeswap_factory_test"] if self.test else addr["pancakeswap_factory"]
        self.router_addr = addr["pancakeswap_router"] if self.test else addr["pancakeswap_router_test"]
        self.factory_abi = abi['pancakeswap_factory']
        self.router_abi = abi['pancakeswap_router']
        self.factory_contract, self.router_contract = None, None
        self._setup()

    def _setup(self) -> None:
        self.w3 = Web3(Web3.HTTPProvider(self.rpc_endpoint))
        if self.w3.isConnected():
            # initialize contract instances for factory and router
            self.factory_contract = self.w3.eth.contract(address = self.factory_addr, abi = self.factory_abi)
            self.router_contract =  self.w3.eth.contract(address = self.router_addr, abi = self.router_abi)
        else:
            raise "incorrect rpc endpoint"

    #listen to the pool and execute trade when matching pair is created
    async def event_handler(self, event):
        print(f"Event: {Web3.toJSON(event)}")
        print(f"Block: {Web3.toJSON(event['blockNumber'])}")
        print(f"TxHash: {Web3.toJSON(event['transactionHash'])}")
        print(f"Token 0: {Web3.toJSON(event['args']['token0'])}")
        print(f"Token 1: {Web3.toJSON(event['args']['token1'])}")

        token0 = Web3.toJSON(event['args']['token0'])
        token1 = Web3.toJSON(event['args']['token1'])

        t2s = self.w3.toChecksumAddress(self.token_2_spend_addr)
        t2b = self.w3.toChecksumAddress(self.token_2_buy_addr)

        if(token0.lower().strip('"') == t2s and token1.lower().strip('"') == t2b) or (token0.lower().strip('"') == t2b and token1.lower().strip('"') == t2s):
            # TODO add function to determine optimal amount of trade
            self._trade(0.001)
        else:
            print("No matching pair, keep looping")
        await asyncio.sleep(0.1)
    
    def run(self):
        loop = asyncio.get_event_loop()
        event = self.factory_contract.events.PairCreated.createFilter(fromBlock="latest")
        try:
            loop.run_until_complete(
                asyncio.gather(
                    self.event_filter(event)
                )
            )
        finally:
            loop.close()

    async def event_filter(self, events):
        while True:
            if len(events.get_new_entries()) == 0:
                print("No pair created yet")
            else:
                for event in events.get_new_entries():
                    await self.event_handler(event)

    def _trade(self, amount:float) -> None:
        '''
        amount: float
            - in ether
        '''

        # get addrerssed of the spend/buy tokens
        t2s = self.w3.toChecksumAddress(self.token_2_spend_addr)
        t2b = self.w3.toChecksumAddress(self.token_2_buy_addr)

        transaction = {
            "from": self.wallet_addr,
            "value": self.w3.toWei(amount,'ether'),
            "gas":25000,
            "gasPrice":self.w3.toWei(self.gas,'gwei'),
            "nounce": self.w3.eth.get_transaction_count(self.wallet_addr)
        }

        # https://docs.pancakeswap.finance/code/smart-contracts/pancakeswap-exchange/router-v2#swapexactethfortokens
        tx = self.router_contract.functions.swapExactETHForTokens(
            # set slippage tolerance, default to 0
            self.slippage,
            # specify path
            [t2s, t2b],
            # user address
            self.wallet_addr,
            # expiration
            time.time()+1000
        ).buildTransactrun(transaction)
    
        # sign the transaction
        signature = self.w3.eth.acccount.sign_transaction(tx, self.private_key)
        # send the transaction
        tx_hex_byte = self.w3.eth.send_raw_transaction(signature.rawTransaction) 

        if tx_hex_byte:
            print(f"Transaction{tx_hex_byte} Successfull")


    

    



